    private void updateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_updateActionPerformed
        // TODO add your handling code here:
        liveScore ls = new liveScore();
        ls.runshow.setText(run.getText());
        ls.wicketshow.setText(wik.getText());
        ls.title2.setText(jTextField1.getText()+" VS "+jTextField2.getText());
        ls.overshow.setText(ovrd.getText());
        ls.batting.setText(jTextField1.getText()+" INNINGS");
        if(ovr == 120){
            ls.title1.setText("ICC INTERNATIONAL T-20 MATCH");
        }
        else{
            ls.title1.setText("ICC INTERNATIONAL ODI MATCH");
        }
        
        ls.setVisible(true);
        this.dispose();
    }//GEN-LAST:event_updateActionPerformed

